import React from "react";
// TODO: import useFormik from formik library
import {useFormik} from "formik"


export default function App() {
  // TODO: add a const called formik assigned to useFormik()
  const formik = useFormik({
    initialValues:{
      name:"",
      email:"",
      password:"",
    },
    onSubmit: values => {
      console.log('form:', values);
    },
    validate: values => {
      let errors={};
      if(!values.name) errors.name = "Field required"
      if(!values.email) errors.email = "Username should be an email"
    }
  })
  return (
    <div onSubmit={formik.handleSubmit}>
      <form>
        <div>Name</div>
        <input type="text" name="name" onChange={formik.handleChange} values={formik.values.name} placeholder="Enter name" />
        <div>Email</div>
        <input type="emzil" name="email" onChange={formik.handleChange} values={formik.values.email} placeholder="Enter email" />
        <div>Password</div>
        <input type="text" name="password" onChange={formik.handleChange} values={formik.values.password} placeholder="Enter password" />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
